sys_ui_action.script:
var rec = new GlideRecord('sysauto_script');
rec.get('name', 'SPF Process Active Records');

//check document key on sys_trigger to find if job is already running
var JobID = rec.sys_id;
var grTrigger = new GlideRecord('sys_trigger');
grTrigger.get('document_key',JobID); //sys id of scheduled job
if (grTrigger.getRowCount()>= 1 && grTrigger.state == 1) { //state == Running
	gs.info("Job already running for SPF Process Active Records sys_id:"
			+ grTrigger.sys_id + "Executed on:" + grTrigger.sys_created_on + "Executed by: "+grTrigger.sys_created_by );

	gs.addInfoMessage("Job already running for SPF Process Active Records "
			+ grTrigger.sys_id + "\nExecuted on:" + grTrigger.sys_created_on + "\nExecuted by: "+grTrigger.sys_created_by );

}else{
//gs.info("SPF process Active Records sys_id " + JobID);
gs.executeNow(rec);
}
