sys_ui_action.script:
//find if job is already running for this configuration
var parm1 = current.sys_id;
var parm2 = gs.getUserName();
var name = "x_tdbf2_server_pat.SPF.ProcessMe";
var Sysevent = new global.spfUTILglobal; //script include helper to find current state whether this job is already been processed
var returnvalue = Sysevent.findEvent(name, parm1); //function call in script include
//gs.addInfoMessage("Value in Sysevent is " + returnvalue);

if (returnvalue == true){ //if this event is running 
	gs.addInfoMessage("SPF - This configuration is currently been processed, was sent by " + parm2);
}else{
gs.addInfoMessage("Sending this record to eventQueue for background processing   ==>" + current.sys_id);
gs.eventQueue("x_tdbf2_server_pat.SPF.ProcessMe", current, parm1,parm2); //send to event registry which will trigger script action SPF process record
}
