//getURL2();
function getURL2() {
     //var envSelected = g_form.getValue('environment');
    var env = g_form.getValue('environment');
    var dac = g_form.getValue('deployment_application_code');
    var os = g_form.getValue('os_family');
    var loc = g_form.getValue('location');
    var support = g_form.getValue("level_2_support_group");
	var vst = g_form.getValue("virtual_server_technology");
//	var hn = g_form.getValue("hostname");


    var ga = new GlideAjax("spfUTIL");

    ga.addParam("sysparm_name", "getURL"); //parameter to call function name in script include
    ga.addParam("sysparm_env", env); //parameter to pass the value from g_form field to script include function
    ga.addParam("sysparm_dac", dac);
    ga.addParam("sysparm_os", os);
    ga.addParam("sysparm_loc", loc);
    ga.addParam("sysparm_support", support);
	ga.addParam("sysparm_vst", vst);
	//ga.addParam("sysparm_hn", hn);


    //myEnv.getXMLAnswer(HelloWorldParse);
    ga.getXML(callback);

    function callback(response) {
        //var serverObj = JSON.parse(response) ; //parse response back from server side (script include) code 
        //console.log(response);
        var answer = response.responseXML.documentElement.getAttribute('answer');
     //   alert(answer);
        answer = answer.evalJSON(); //transform json string to an object
      //  g_form.setValue('notes', answer.notes);
        g_form.setValue('url', answer.url);
		//alert(answer.url);
		//g_form.setValue('history', history);
		g_form.save();
    }
}
