sys_ui_action.script:
function RunPatchActivities() {
    var id = g_form.getUniqueValue();
    var env = g_form.getValue('environment');
    var dac = g_form.getValue('deployment_application_code');
    var os = g_form.getValue('os_family');
    var loc = g_form.getValue('location');
    var support = g_form.getValue("level_2_support_group");
    var vst = g_form.getValue("virtual_server_technology");
    //fields below sent to script include to be used to update server records that v find by formulating query frm fields above
    var patchGroup = g_form.getValue("patching_team");
    var patchDays = g_form.getValue("patch_day_of_month");
    var patchStartTime = g_form.getValue("patch_start_time");
    //  var hn = g_form.getValue("hostname");

    var ga = new GlideAjax("spfUTIL");
    ga.addParam('sysparm_name', 'patchMe2'); //parameter to call function name in script include

    //parameter to pass the value from g_form field to script include function
    ga.addParam("sysparm_id", id);
    ga.addParam("sysparm_env", env);
    ga.addParam("sysparm_dac", dac);
    ga.addParam("sysparm_os", os);
    ga.addParam("sysparm_loc", loc);
    ga.addParam("sysparm_support", support);
    ga.addParam("sysparm_vst", vst);
    ga.addParam("sysparm_patchGroup", patchGroup);
    ga.addParam("sysparm_patchDays", patchDays);
    ga.addParam("sysparm_patchStartTime", patchStartTime);
    //ga.addParam("sysparm_hn", hn);

    //debugger;
    //myEnv.getXMLAnswer(HelloWorldParse);
    //now get ready for patching server side if answer is true
    var question = "This will add/update patch details for all Servers identified in the URL, Sure?";
    var ans = confirm(question);
    if (ans == true) {
        g_form.showFieldMsg('number', 'Patching operation executed...');
        //location.reload(); //reload form

        //  showLoadingDialog();
        ga.getXML(callback); //this is to execute server side code
        g_form.save();
    } else {
        g_form.showFieldMsg('number', 'Patching operation cancelled...');
        return false;
    }
}

function callback(response) {
    var answer = response.responseXML.documentElement.getAttribute('answer');
    //alert(answer);
    answer = answer.evalJSON(); //transform json string to an object
    g_form.showFieldMsg('number', 'Patching operation completed...');
    //g_form.setValue('history',answer.feedBack); //coders fyi..setValue method not supported for journal field
    alert(answer.feedBack);

}
