sysevent_script_action.script:
//script to process Active records from spf mapping table
gs.info("SPF - in script action process record");
var TABLE_NAME = "x_tdbf2_server_pat_spf";
var grSPF = new GlideRecord(TABLE_NAME);
var ID = event.parm1.toString(); //parameter from eventQueue
var UserID = event.parm2.toString();
grSPF.addQuery('sys_id', ID);
grSPF.query();
gs.info("SPF in script action, value in ID is " + ID);
//process this configuration
while (grSPF.next()) {
    var number = grSPF.number;
    //gather variables
    var os = grSPF.os_family;
    //  gs.info("value in os is  " + os);
    var dac = grSPF.deployment_application_code;
    var vst = grSPF.virtual_server_technology;
    var env = grSPF.environment;
    var support = grSPF.level_2_support_group;
    var loc = grSPF.location;

    //get patch data
    var patchGroup = grSPF.patching_team;
    var patchDays = grSPF.patch_day_of_month;
    var patchStartTime = grSPF.patch_start_time;

    //now find servers matching criteria
    var gr = new GlideRecord('cmdb_ci_server');
    var minOneFieldInQuery = "false";
    if (!env == '') {
        gr.addQuery('u_os_environment', env);
        minOneFieldInQuery = "true";
    }
    if (!dac == '') {
        gr.addQuery('u_deployment_application_code', dac);
        minOneFieldInQuery = "true";
    }
    if (!os == '') {
        gr.addQuery('u_os_family', os);
        minOneFieldInQuery = "true";
    }
    if (!loc == '') {
        gr.addQuery('location', loc);
        minOneFieldInQuery = "true";
    }
    if (!support == '') {
        gr.addQuery('u_support_group_l2', support);
        minOneFieldInQuery = "true";
    }
    if (!vst == '') {
        gr.addQuery('u_virtual_server_technology', vst);
        minOneFieldInQuery = "true";
    }

    // exclude server where u_patch_data_source is "manual"
    gr.addQuery('asset.install_status', '!=', 7).addOrCondition('asset.install_status', ''); //exclude retired
    gr.addQuery('u_patch_data_source', '!=', 'Manual').addOrCondition('u_patch_data_source', '');

    if (minOneFieldInQuery == "true") {
        //process only if min one field is sent in quer
        gr.query();
        gs.info(grSPF.number + " Encoded query in script action is==> " + gr.getEncodedQuery());
        gr.setValue('u_os_patching_team', patchGroup);
        gr.setValue('u_patch_day_of_month', patchDays);
        gr.setValue('u_patch_start_time', patchStartTime);
        gr.updateMultiple();

        var strQry = gr.getEncodedQuery();
        var instanceURL = gs.getProperty('glide.servlet.uri');
        strQry = instanceURL + "cmdb_ci_server_list.do?sysparm_query=" + strQry;
        var hist = 'Records Updated: ' + gr.getRowCount() + '\n'; 
		hist = hist + 'URL: ' + strQry + '\n';
		hist = hist + 'Updated by User UI action' + '\n';
		hist = hist + 'USER ID: ' + UserID + '\n';
		hist = hist + 'Patching team: ' + grSPF.patching_team.name + '\n';
		hist = hist + 'Patch day of month: '+ patchDays + '\n';
		hist = hist + 'Patch start time: ' + patchStartTime + '\n';

        gs.info(number + ":Updated total via eventQueue: " + gr.getRowCount());
    } else {
        gs.info("SPF scheduled script no records updated for query " + gr.getEncodedQuery());
    }
    grSPF.history = hist;
    grSPF.update();

}
